mapper.select:
		SELECT

          ${returnFields}




		FROM mmc_coupon

					        		    				AND id = #{id,jdbcType=INTEGER}

						 #{item,jdbcType=INTEGER}

            								        		    				AND owner_type = #{ownerType,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            								        		    				AND owner_id = #{ownerId,jdbcType=VARCHAR}
    														 AND owner_id LIKE concat(#{ownerIdLike,jdbcType=VARCHAR},'%')
														        		    				AND owner_name = #{ownerName,jdbcType=VARCHAR}
    														 AND owner_name LIKE concat(#{ownerNameLike,jdbcType=VARCHAR},'%')
														        		    				AND activity_id = #{activityId,jdbcType=INTEGER}

						 #{item,jdbcType=INTEGER}

            								        		    				AND coupon_name = #{couponName,jdbcType=VARCHAR}
    														 AND coupon_name LIKE concat(#{couponNameLike,jdbcType=VARCHAR},'%')
														        		    				AND coupon_use_type = #{couponUseType,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            								        		    				AND coupon_no = #{couponNo,jdbcType=VARCHAR}
    														 AND coupon_no LIKE concat(#{couponNoLike,jdbcType=VARCHAR},'%')

						 #{item,jdbcType=VARCHAR}

            																				      AND price >= #{priceFrom,jdbcType=DECIMAL}
				  AND price <= #{priceTo,jdbcType=DECIMAL}
								        		    				AND coupon_package_id = #{couponPackageId,jdbcType=INTEGER}

						 #{item,jdbcType=INTEGER}

            								        		    				AND per_person_limit = #{perPersonLimit,jdbcType=INTEGER}

						 #{item,jdbcType=INTEGER}

            								        		    				AND per_order_limit = #{perOrderLimit,jdbcType=INTEGER}

						 #{item,jdbcType=INTEGER}

            								        		    				AND coupon_type_id = #{couponTypeId,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            																				      AND start_time >= #{startTimeFrom,jdbcType=TIMESTAMP}
				  AND start_time <= #{startTimeTo,jdbcType=TIMESTAMP}
																				      AND end_time >= #{endTimeFrom,jdbcType=TIMESTAMP}
				  AND end_time <= #{endTimeTo,jdbcType=TIMESTAMP}
																				      AND pending_time >= #{pendingTimeFrom,jdbcType=TIMESTAMP}
				  AND pending_time <= #{pendingTimeTo,jdbcType=TIMESTAMP}
								        		    				AND is_shared_subpromotion = #{isSharedSubpromotion,jdbcType=BOOLEAN}

						 #{item,jdbcType=BOOLEAN}

            								        		    				AND is_shared_coupon = #{isSharedCoupon,jdbcType=BOOLEAN}

						 #{item,jdbcType=BOOLEAN}

            								        		    				AND redirect_type = #{redirectType,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            																						        		    				AND is_must_apply = #{isMustApply,jdbcType=BOOLEAN}

						 #{item,jdbcType=BOOLEAN}

            								        		    				AND afford_cost_type = #{affordCostType,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            																				      AND afford_cost_value >= #{affordCostValueFrom,jdbcType=DECIMAL}
				  AND afford_cost_value <= #{affordCostValueTo,jdbcType=DECIMAL}
								        		    				AND coupon_dispatch_type = #{couponDispatchType,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            																				      AND issue_start_time >= #{issueStartTimeFrom,jdbcType=TIMESTAMP}
				  AND issue_start_time <= #{issueStartTimeTo,jdbcType=TIMESTAMP}
																				      AND issue_end_time >= #{issueEndTimeFrom,jdbcType=TIMESTAMP}
				  AND issue_end_time <= #{issueEndTimeTo,jdbcType=TIMESTAMP}
								        		    				AND issued_total_amount = #{issuedTotalAmount,jdbcType=INTEGER}

						 #{item,jdbcType=INTEGER}

            								        		    				AND scope_type = #{scopeType,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            																				      AND promotion_amount >= #{promotionAmountFrom,jdbcType=DECIMAL}
				  AND promotion_amount <= #{promotionAmountTo,jdbcType=DECIMAL}
																				      AND order_full_amount >= #{orderFullAmountFrom,jdbcType=DECIMAL}
				  AND order_full_amount <= #{orderFullAmountTo,jdbcType=DECIMAL}
																				      AND promotion_max_amount >= #{promotionMaxAmountFrom,jdbcType=DECIMAL}
				  AND promotion_max_amount <= #{promotionMaxAmountTo,jdbcType=DECIMAL}
																				      AND discount_amount >= #{discountAmountFrom,jdbcType=DECIMAL}
				  AND discount_amount <= #{discountAmountTo,jdbcType=DECIMAL}
								        		    				AND gift_name = #{giftName,jdbcType=VARCHAR}
    														 AND gift_name LIKE concat(#{giftNameLike,jdbcType=VARCHAR},'%')
																												        		    				AND apply_audit_name = #{applyAuditName,jdbcType=VARCHAR}
    														 AND apply_audit_name LIKE concat(#{applyAuditNameLike,jdbcType=VARCHAR},'%')
																										      AND apply_audit_time >= #{applyAuditTimeFrom,jdbcType=TIMESTAMP}
				  AND apply_audit_time <= #{applyAuditTimeTo,jdbcType=TIMESTAMP}
																				      AND create_time >= #{createTimeFrom,jdbcType=TIMESTAMP}
				  AND create_time <= #{createTimeTo,jdbcType=TIMESTAMP}
								        		    				AND create_user = #{createUser,jdbcType=VARCHAR}
    														 AND create_user LIKE concat(#{createUserLike,jdbcType=VARCHAR},'%')
														        		    				AND create_user_name = #{createUserName,jdbcType=VARCHAR}
    														 AND create_user_name LIKE concat(#{createUserNameLike,jdbcType=VARCHAR},'%')
																										      AND update_time >= #{updateTimeFrom,jdbcType=TIMESTAMP}
				  AND update_time <= #{updateTimeTo,jdbcType=TIMESTAMP}
								        		    				AND update_user = #{updateUser,jdbcType=VARCHAR}
    														 AND update_user LIKE concat(#{updateUserLike,jdbcType=VARCHAR},'%')
														        		    				AND update_user_name = #{updateUserName,jdbcType=VARCHAR}
    														 AND update_user_name LIKE concat(#{updateUserNameLike,jdbcType=VARCHAR},'%')
														        		    				AND is_deleted = #{isDeleted,jdbcType=BOOLEAN}

						 #{item,jdbcType=BOOLEAN}

            								        		    				AND status = #{status,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            								        		    				AND audit_status = #{auditStatus,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            								        		    				AND rebate_sender = #{rebateSender,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}

            								        		    				AND ext_relation_id = #{extRelationId,jdbcType=VARCHAR}
    														 AND ext_relation_id LIKE concat(#{extRelationIdLike,jdbcType=VARCHAR},'%')

						 #{item,jdbcType=VARCHAR}

            								        		    				AND ext_relation_name = #{extRelationName,jdbcType=VARCHAR}
    														 AND ext_relation_name LIKE concat(#{extRelationNameLike,jdbcType=VARCHAR},'%')

						 #{item,jdbcType=VARCHAR}

            								        		    				AND process_data_flag = #{processDataFlag,jdbcType=TINYINT}

						 #{item,jdbcType=TINYINT}



			order by ${orderBy} ${order}
			order by id desc
