mapper.select:
SELECT

    ${returnFields}




FROM tc_retrying

    AND id = #{id,jdbcType=BIGINT}


            #{item,jdbcType=BIGINT}


    AND batch_no = #{batchNo,jdbcType=VARCHAR}
    AND batch_no LIKE
        concat(#{batchNoLike,jdbcType=VARCHAR},'%')



            #{item,jdbcType=VARCHAR}


    AND error_code = #{errorCode,jdbcType=VARCHAR}
    AND error_code LIKE
        concat(#{errorCodeLike,jdbcType=VARCHAR},'%')



            #{item,jdbcType=VARCHAR}


    AND error_msg = #{errorMsg,jdbcType=VARCHAR}
    AND delete_flag = #{deleteFlag,jdbcType=TINYINT}


            #{item,jdbcType=TINYINT}


    AND doing_type = #{doingType,jdbcType=TINYINT}


            #{item,jdbcType=TINYINT}


    AND doing_type_name =
        #{doingTypeName,jdbcType=VARCHAR}

    AND doing_type_name LIKE
        concat(#{doingTypeNameLike,jdbcType=VARCHAR},'%')


        #{item,jdbcType=VARCHAR}


    AND final_result = #{finalResult,jdbcType=TINYINT}


            #{item,jdbcType=TINYINT}


    AND retry_count = #{retryCount,jdbcType=INTEGER}


            #{item,jdbcType=INTEGER}


    AND content = #{content,jdbcType=LONGVARCHAR}
    AND trade_scene_no = #{tradeSceneNo,jdbcType=VARCHAR}

    AND trade_scene_no LIKE
        concat(#{tradeSceneNoLike,jdbcType=VARCHAR},'%')


        #{item,jdbcType=VARCHAR}


    AND create_time >= #{createTimeFrom,jdbcType=TIMESTAMP}
    AND create_time <= #{createTimeTo,jdbcType=TIMESTAMP}
    AND create_user = #{createUser,jdbcType=VARCHAR}
    AND create_user LIKE
        concat(#{createUserLike,jdbcType=VARCHAR},'%')

    AND create_user_name =
        #{createUserName,jdbcType=VARCHAR}

    AND create_user_name LIKE
        concat(#{createUserNameLike,jdbcType=VARCHAR},'%')

    AND update_time >= #{updateTimeFrom,jdbcType=TIMESTAMP}
    AND update_time <= #{updateTimeTo,jdbcType=TIMESTAMP}
    AND update_user = #{updateUser,jdbcType=VARCHAR}
    AND update_user LIKE
        concat(#{updateUserLike,jdbcType=VARCHAR},'%')

    AND update_user_name =
        #{updateUserName,jdbcType=VARCHAR}

    AND update_user_name LIKE
        concat(#{updateUserNameLike,jdbcType=VARCHAR},'%')

    AND is_deleted = #{isDeleted,jdbcType=BOOLEAN}


            #{item,jdbcType=BOOLEAN}




    order by ${orderBy} ${order}
    order by id desc
